               static VALUE
env_to_s(void)
{
    return rb_usascii_str_new2("ENV");
}


               static VALUE
env_update(VALUE env, VALUE hash)
{
    if (env == hash) return env;
    hash = to_hash(hash);
    rb_hash_foreach(hash, env_update_i, 0);
    return env;
}


